package fpl.soa.reviewservice.service;


import fpl.soa.reviewservice.RestClients.CustomerRestClient;
import fpl.soa.reviewservice.entities.Review;
import fpl.soa.reviewservice.model.PageInfo;
import fpl.soa.reviewservice.repos.ReviewRepo;
import lombok.RequiredArgsConstructor;
import org.springframework.data.domain.Page;
import org.springframework.data.domain.PageRequest;
import org.springframework.data.domain.Pageable;
import org.springframework.data.domain.Sort;
import org.springframework.stereotype.Service;

import java.time.LocalDate;
import java.util.*;

@Service
@RequiredArgsConstructor
public class ReviewServiceImpl implements ReviewService {

    private final ReviewRepo reviewRepository;
    private final CustomerRestClient customerClient;

    public Review addReview(Review review) {
        review.setReviewId(UUID.randomUUID().toString());
        review.setReviewDate(new Date());
        if (review.getCustomerProfileImageBase64().equals("User_Image")){
            review.setCustomerProfileImageBase64(customerClient.getCustomerImageBase64ByEmail(review.getCustomerEmail()));
        }
        else if (review.getCustomerProfileImageBase64().equals("No_Image")){
            review.setCustomerProfileImageBase64("data:image/jpeg;base64,");
        }
        return reviewRepository.save(review);
    }


    @Override
    public Page<Review> getReviewsByProductId(String productId, int page, int size) {
        Pageable pageable = PageRequest.of(page, size,
                Sort.by(Sort.Direction.DESC, "reviewDate"));
        return reviewRepository.findByProductId(productId, pageable);
    }

    @Override
    public void initReview() {

    }

    @Override
    public PageInfo getProductReviewsPageInfo(int size) {
        return null;
    }


    @Override
    public Review updateReview(Review review) {
        return null;
    }

    @Override
    public void deleteReview(String reviewId) {
        reviewRepository.deleteById(reviewId);
    }

    @Override
    public Map<String, Object> getReviewStats(String productId) {
        List<Review> reviews = reviewRepository.findByProductId(productId);
        double average = reviews.stream().mapToInt(Review::getRating).average().orElse(0.0);
        int total = reviews.size();

        Map<String, Object> stats = new HashMap<>();
        stats.put("averageRating", average);
        stats.put("totalReviews", total);
        return stats;
    }
}
