    @Override
    public Product reserve(Product desiredProduct, String orderId) {
        Product productEntity = productRepo.findById(desiredProduct.getProductId())
                .orElseThrow(() -> new RuntimeException("Product not found"));

        // Extract desired size, color, and quantity from desiredProduct variants
        // Assuming desiredProduct has exactly one sizeVariant with one colorVariant selected for reservation

        if (desiredProduct.getSizeVariants() == null || desiredProduct.getSizeVariants().isEmpty()) {
            throw new RuntimeException("No size variant provided");
        }
        SizeVariant desiredSizeVariant = desiredProduct.getSizeVariants().get(0);

        if (desiredSizeVariant.getColorVariants() == null || desiredSizeVariant.getColorVariants().isEmpty()) {
            throw new RuntimeException("No color variant provided");
        }
        ColorVariant desiredColorVariant = desiredSizeVariant.getColorVariants().get(0);

        String desiredSize = desiredSizeVariant.getSize();
        String desiredColor = desiredColorVariant.getColor();
        int desiredQuantity = desiredColorVariant.getQuantity();

        boolean reserved = false;

        for (SizeVariant sizeVariant : productEntity.getSizeVariants()) {
            if (sizeVariant.getSize().equals(desiredSize)) {
                for (ColorVariant colorVariant : sizeVariant.getColorVariants()) {
                    if (colorVariant.getColor().equals(desiredColor)) {
                        if (colorVariant.getQuantity() < desiredQuantity) {
                            throw new ProductInsufficientQuantityException(productEntity.getProductId(), orderId);
                        }
                        colorVariant.setQuantity(colorVariant.getQuantity() - desiredQuantity);
                        reserved = true;
                        break;
                    }
                }
                if (reserved) break;
            }
        }

        if (!reserved) {
            throw new RuntimeException("Desired size/color variant not found");
        }

        return productRepo.save(productEntity);
    }

    @Override
    public void cancelReservation(Product productToCancel, String orderId) {
        Product productEntity = productRepo.findById(productToCancel.getProductId())
                .orElseThrow(() -> new RuntimeException("Product not found"));

        if (productToCancel.getSizeVariants() == null || productToCancel.getSizeVariants().isEmpty()) {
            throw new RuntimeException("No size variant provided");
        }
        SizeVariant cancelSizeVariant = productToCancel.getSizeVariants().get(0);

        if (cancelSizeVariant.getColorVariants() == null || cancelSizeVariant.getColorVariants().isEmpty()) {
            throw new RuntimeException("No color variant provided");
        }
        ColorVariant cancelColorVariant = cancelSizeVariant.getColorVariants().get(0);

        String cancelSize = cancelSizeVariant.getSize();
        String cancelColor = cancelColorVariant.getColor();
        int cancelQuantity = cancelColorVariant.getQuantity();

        boolean canceled = false;

        for (SizeVariant sizeVariant : productEntity.getSizeVariants()) {
            if (sizeVariant.getSize().equals(cancelSize)) {
                for (ColorVariant colorVariant : sizeVariant.getColorVariants()) {
                    if (colorVariant.getColor().equals(cancelColor)) {
                        colorVariant.setQuantity(colorVariant.getQuantity() + cancelQuantity);
                        canceled = true;
                        break;
                    }
                }
                if (canceled) break;
            }
        }

        if (!canceled) {
            throw new RuntimeException("Cancel size/color variant not found");
        }

        productRepo.save(productEntity);
    }


