    @Override
    public ProductVariantDetails getProductBySizeAndColor(String productId, String size, String color) {
        Product product = productRepo.findById(productId)
                .orElseThrow(() -> new RuntimeException("Product not found with ID: " + productId));

        SizeVariant sizeVariant = product.getSizeVariants()
                .stream()
                .filter(sv -> sv.getSize().equalsIgnoreCase(size))
                .findFirst()
                .orElseThrow(() -> new RuntimeException("Size '" + size + "' not found for product ID: " + productId));

        ColorVariant colorVariant = sizeVariant.getColorVariants()
                .stream()
                .filter(cv -> cv.getColor().equalsIgnoreCase(color))
                .findFirst()
                .orElseThrow(() -> new RuntimeException("Color '" + color + "' not found for size '" + size + "'"));

        return ProductVariantDetails.builder()
                .productId(product.getProductId())
                .name(product.getName())
                .productPrice(sizeVariant.getProductPrice())
                .originLocation(product.getOriginLocation())
                .productImagesBas64(colorVariant.getProductImagesBas64())
                .build();
    }



    @Override
    public PageInfo getProductPageInfo(int size) {
        PageInfo pageInfo = PageInfo.builder()
                .totalElements(this.productRepo.findAll().size())
                .totalPages(this.productRepo.findAll(PageRequest.of(0, size)).getTotalPages())
                .size(size)
                .build();
        return pageInfo;
    }
